	static void retrievedata() {
		
		try {
			
			String strURL = url2 + uid;
			
			URL obj = new URL(strURL);
			HttpURLConnection con = (HttpURLConnection) obj.openConnection();
			con.setRequestMethod("GET");
			con.setRequestProperty("User-Agent", "PostmanRuntime/7.15.2");
			con.setRequestProperty("Host", "www.khelmahakumbh.org");
			int responseCode = con.getResponseCode();
			System.out.println("GET Response Code :: " + responseCode);
			if (responseCode == HttpURLConnection.HTTP_OK) { // success
				BufferedReader in = new BufferedReader(new InputStreamReader(
						con.getInputStream(), "UTF-8"));
				String inputLine;
				StringBuffer response = new StringBuffer();

				while ((inputLine = in.readLine()) != null) {
					response.append(inputLine);
				}
				in.close();

				parseHTML(response.toString());
				
				// print result
				System.out.println(response.toString());
			} else {
				System.out.println("GET request not worked");
			}
			
		} catch (Exception e) {
			e.printStackTrace();
		}
	}
	
	static void parseHTML(String string) throws IOException {
		
		
		if(string != null) {
			Document htmlFile = null;
	        htmlFile = Jsoup.parse(string);
	        System.out.println(htmlFile.baseUri());
	        
	        Elements tab = htmlFile.select("table tr");
	        for (Element element : tab) {
	        	Elements tds = element.select("td");
	        	for (Element element2 : tds) {
	        		System.out.println(element2.text());					
				}
			}
	        
		}
		
	}
	
